/**
 * https://leetcode.com/problems/number-of-lines-to-write-string/
 * 
 * @param {number[]} widths
 * @param {string} S
 * @return {number[]}
 */
const numberOfLines = (widths, S) => {
    
    let result = [0];
    let index = 0;

    [...S].forEach( (el) => {
        
        let value = widths[el.charCodeAt(0) - 97];

        if( result[index] + value > 100) {
            result.push(value);
            index++;
        } else {
            result[index] = result[index] + value; 
        }
    });
    
    return [
        result.length,
        result[result.length - 1]
    ];
}

numberOfLines([10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10], "abcdefghijklmnopqrstuvwxyz");

